    private void jMenuItemAbrirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItemAbrirActionPerformed
        if (arquivo.abrirArquivo(this)) {
            jTextAreaTexto.setText(arquivo.ler());
            this.setTitle(arquivo.getArquivo().getAbsolutePath());
        }
    }//GEN-LAST:event_jMenuItemAbrirActionPerformed

    private void jMenuItemSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItemSalvarActionPerformed
        if (this.salvar()) {
            this.setTitle(arquivo.getArquivo().getAbsolutePath());
        }
    }//GEN-LAST:event_jMenuItemSalvarActionPerformed

    private void jMenuItemSalvarComoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItemSalvarComoActionPerformed
        if (arquivo.salvarComo(jTextAreaTexto.getText(), this)) {
            this.setTitle(arquivo.getArquivo().getAbsolutePath());
        }
    }//GEN-LAST:event_jMenuItemSalvarComoActionPerformed

    private void jMenuItemSairActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItemSairActionPerformed
        int result = JOptionPane.showConfirmDialog(this, "Deseja salvar este arquivo?");
        switch (result) {
            case JOptionPane.CANCEL_OPTION:
                return;
            case JOptionPane.OK_OPTION:
                this.salvar();
            case JOptionPane.NO_OPTION:
                System.exit(0);
            default:
                break;
        }
    }//GEN-LAST:event_jMenuItemSairActionPerformed

