    private boolean salvar() {
        String texto = jTextAreaTexto.getText();
        if (arquivo.getArquivo() == null) {
            return arquivo.salvarComo(texto, this);
        } else {
            if (!arquivo.escrever(texto, false)) {
                return false;
            }
            return arquivo.salvar();
        }
    }
